import pytest
import io

N = 5

def json_data(s, n=N):
    for i in range(n):
        yield '{"id":' + str(i) + '}'

def get_text(s, n=N):
        return "\n".join(list(json_data(s, n)))

@pytest.fixture
def input_streams():
    return io.StringIO(get_text(0)), \
           io.StringIO(get_text(10)),\
           io.StringIO(get_text(20, n=2))
